package javapractise;

public class que25 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum = 0;
		int[] arrayVar = {7,8,55,9,2};
		for(int a: arrayVar)
		{
		sum += a;
		}
		float average = sum / 4;
		System.out.println("Average of array elements is: " + average);
		

	}

}
